// controllers/userController.js
const jwt = require('jsonwebtoken')
const bcrypt = require('bcrypt');
const config = require('../config/config');
const User = require('../models/User');
const Profile = require('../models/Profile');

const JWT_KEY = config.jwt_key;

exports.loginUser = async (req, res, next) => {
    try {
        const { email, password } = req.body;
        // Find the user by username
        const user = await User.findOne({ email });
        if (!user) {
            return res.status(401).json({ message: 'Invalid username or password' });
        }
        // Check if the password is correct
        const isPasswordValid = await bcrypt.compare(password, user.password);
        if (!isPasswordValid) {
            return res.status(401).json({ message: 'Invalid username or password' });
        }
        // Generate JWT token
        const token = jwt.sign({ id: user._id, email: user.email, username: user.username }, JWT_KEY, { expiresIn: "1h" });
        res.status(200).json({ token });
    } catch (error) {
        console.error(error);
        res.status(500).json({ message: 'Internal server error' });
    }
};

exports.registerUser = async (req, res, next) => {
        try {
            const { username, email, password } = req.body;
            if(!username || !email || !password) res.status(400).json({ message: 'Provide all fields' });
            // Check if the username is already taken
            const existingUser = await User.findOne({ email });
            if (existingUser) {
                return res.status(400).json({ message: 'Email is already taken' });
            }
            // Hash the password
            const hashedPassword = await bcrypt.hash(password, 10);
            // Create new user
            const newUser = new User({ username, email, password: hashedPassword });
            await newUser.save();
            const newProfile = new Profile(
                {
                    imageUrl: {
                        data: 'data:image/jpeg;base64,', // Set the default image data here
                        contentType: 'image/jpeg' // Set the default content type here
                    },
                    firstName: '',
                    lastName: '',
                    email: email,
                    phoneNumber: '',
                    address: '',
                    language: 'English',
                    timeZone: 'GMT',
                    currency: 'INR'
                }
            );
            await newProfile.save();
            res.status(201).json({ message: 'User registered successfully' });
        } catch (error) {
            console.error(error);
            res.status(500).json({ message: 'Internal server error' });
        }
        // console.log(req.body)
        // res.status(201).json({'message': "API working... "});
}


